import { fork, take } from "redux-saga/effects";
import { increment } from "../store/countReducer";
import countWhatcher from "./sagaCount";

function* loggerSaga() {
    yield take(increment, () => console.log('azaza'))
}

function* rootSaga() {
    yield fork(countWhatcher)
    yield fork(loggerSaga)
}

export default rootSaga;
