export const GRID_POSITION = true;
export const VERTICAL_POSITION = false;

const defaultState = {
    state: GRID_POSITION
}

export const configurationReducer = (state = defaultState, action) => {
    switch (action.type) {
        case "CHANGE_POSITION":
          return {...state, position: payload}
        default:
          return state;
    }
}

export const getState = state => state.configuration?.state;
