import {put, takeEvery } from 'redux-saga';
import { select } from 'redux-saga/effects';
import { getState } from '../store/configurationReducer';
import { getCount, isBlocked } from '../store/countReducer';

export function* infoWorker() {
    const blocked = yield select([isBlocked, getCount, getState]);
    const blockedInfo = blocked ? "блокировка" : "нет";
    yield ([positionText, count, blockedInfo].join());
}

export function* infoWatcher () {
    yield takeEvery(increment, countWorker);
}

export default infoWatcher;
