import {put, takeEvery } from 'redux-saga';
import { ADD_1, increment } from '../store/countReducer';

const delay = (ms) => new Promise( res => setTimeout(res, ms))

export function* countWorker () {
    yield delay(1000)
    yield put(increment)
}

export function* countWhatcher () {
    yield takeEvery(ADD_1, countWorker);
}
