const defaultState = {
    count: 0,
    blocked: false
}

export const countReducer = (state = defaultState, { type, payload }) => {
  switch (type) {
    case "ADD_1":
      return {...state, count: (state.blocked === false) ? state.count + 1 : state.count};
    case "SET_COUNT":
      return {...state, count: (state.blocked == 0) ? 0 + Number(payload) : state.count};
    case "TOGGLE_COUNTER":
      return {...state, blocked: !state.blocked };
    default:
      return state;
  }
}
