import { createAction, createReducer } from '@reduxjs/toolkit'

const defaultState = {
    count: 0,
    blocked: false
}

export const increment = createAction('ADD_1');
export const setCount = createAction('SET_COUNT', payload => ({payload}));
export const toggleCounter = createAction('TOGGLE_COUNTER');

export const countReducer = createReducer(defaultState, {
  [increment]: ({count, blocked, ...restState}) => ({...restState, count: blocked ? count : count + 1}),
  [setCount]: ({count, blocked, ...restState}, {payload}) => ({...restState, count: blocked ? count : Number(payload)}),
  [toggleCounter]: ({blocked, ...restState}) => ({...restState, blocked: !blocked})
});

const {a, b} = {a: 100, b: 100, c: 100, d: 100};
