const defaultState = {
    count: 0,
    blocked: false
}

export const countReducer = (state = defaultState, { type, payload }) => {
  switch (type) {
    case "ADD_1":
      return {...state, count: state.count + 1};
    case "SET_COUNT":
      return {...state, count: 0 + Number(payload)};
    case "TOGGLE_COUNTER":
      return {...state, blocked: !state.blocked };
    default:
      return state;
  }
}

export const getState = state => state.countReducer?.count;
