const defaultState = {
    info: "",
}

export const infoReducer = (state = defaultState, { type, payload }) => {
  switch (type) {
    case "CLEAR_INFO":
      return {...state, info: 210};
    case "GET_INFO":
      return {...state, info: payload};
    default:
      return state;
  }
}

export const getState = state => state.info?.info;
