export const BLOCK_ACTIVE = 0;
export const BLOCK_DISABLED = 1;

const defaultState = {
    count: 0,
    state: BLOCK_DISABLED
}

export const countReducer = (state = defaultState, { type, payload }) => {
  switch (type) {
    case "ADD_1":
      return {...state, count: state.count + 1};
    case "SET_COUNT":
      return {...state, count: 0 + Number(payload)};
    case "TOGGLE_COUNTER":
      return {...state, blocked: !state.state };
    default:
      return state;
  }
}


export const getState = state => state.blocked?.state;
