import React from "react";
import styled from "styled-components";
import Button from "./ui/Button";
import Container from "./ui/Container";
import { useDispatch, useSelector } from "react-redux";
import { getState } from "../store/configurationReducer";

const ContainerInfo = styled.div`
    padding-bottom: 20px;
`

const TextContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    margin-bottom: 4px;
`

const Information = () => {
    const position = useSelector(getState);
    const count = useSelector(state => state.count.count);
    const blocked = useSelector(state => state.count.blocked);

    const positionText = position === 0 ? "СЕТКА" : "ВЕРТИКАЛЬНО";
    const blockedText = blocked ? "ДА" : "НЕТ";

    const infoText = [positionText, count, blockedText].join();

    const dispatch = useDispatch();
    const addInfo = () => dispatch({type: "GET_INFO", payload: infoText})

    return (
        <Container>
            <ContainerInfo>
                <TextContainer>
                    <p>ПОЛОЖЕНИЕ БЛОКОВ:</p><span>{positionText}</span>
                </TextContainer>
                <TextContainer>
                    <p>ЗНАЧЕНИЕ СЧЕТЧИКА:</p><span>{count}</span>
                </TextContainer>
                <TextContainer>
                    <p>БЛОКИРОВКА СЧЕТЧИКА:</p><span>{blockedText}</span>
                </TextContainer>
            </ContainerInfo>
            <Button onClick={addInfo}>ВЫВЕСТИ ИНФУ В БЛОК 4</Button>
        </Container>
    )
}

export default Information;
