import React from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import Button from "./ui/Button";
import Container from "./ui/Container";
import { useState } from "react";

const Form = styled.div`
    display: flex;
    flex-direction: row;
    margin: 14px;
`

const Addition = () => {
    const [value, setValue] = useState(0);
    const bloked = useSelector(state => state.count.bloked);
    const count = useSelector(state => state.count.count);

    const dispatch = useDispatch();
    const setupCounter = () => dispatch({ type: "SET_COUNT", payload: value });
    const incrementCounter = () => dispatch({ type: "ADD_1" });
    const clearInfo = () => dispatch({ type: "CLEAR_INFO", payload: 10});

    const setupCount = () => {(bloked == false) ? setupCounter() : count};
    const incrementCount = (bloked == false) ? incrementCounter() : count;

    return (
        <Container>
            <Button onClick={incrementCount}>ДОБАВИТЬ 1</Button>
            <Form>
                <Button onClick={setupCount}>УСТАНОВИТЬ</Button>
                <input type='number' onChange={event => setValue(event.target.value)}></input>
            </Form>
            <Button onClick={clearInfo}>ПОЧИСТИТЬ ИНФУ В БЛОКЕ 4</Button>
        </Container>
    )
}

export default Addition;
