import { createAction, createReducer } from '@reduxjs/toolkit'

const defaultState = {
    count: 0,
    blocked: false
}

// export const countReducer = (state = defaultState, { type, payload }) => {
//   switch (type) {
//     case "ADD_1":
//       return {...state, count: (state.blocked == 0) ? state.count + 1 : state.count};
//     case "SET_COUNT":
//       return {...state, count: (state.blocked == 0) ? 0 + Number(payload) : state.count};
//     case "TOGGLE_COUNTER":
//       return {...state, blocked: !state.blocked };
//     default:
//       return state;
//   }
// }

const addIncrement1 = createAction('ADD_1')
const addIncrement = createAction('SET_COUNT')
const blockCounter = createAction('TOGGLE_COUNTER')

export const countReducer = createReducer(defaultState, (builder) => {
  builder
    .addCase(addIncrement1, (state, action) => {(state.blocked == 0) ? state.count + 1 : state.count})
    .addCase(addIncrement, (state, action) => {(state.blocked == 0) ? 0 + Number(action.payload) : state.count})
    .addCase(blockCounter, (state, action) => {!state.blocked})
})
