const defaultState = {
    position: "сетка",
    count: 210,
    block: "нет",
}

export const infoReducer = (state = defaultState, action) => {
  switch (action.type) {
    case "ADD_INFO":
      return {...state, count: state.count + action.payload};
    case "CLEAN_INFO":
      return {...state, count: 0 + Number(action.payload)};
    default:
      return state;
  }
}
