export const GRID_POSITION = 0;
export const VERTICAL_POSITION = 1;

const defaultState = {
    state: GRID_POSITION
}

export const configurationReducer = (state = defaultState, action) => {
    switch (action.type) {
        case "CHANGE_POSITION":
          return {...state, state: !state.state}
        default:
          return state;
    }
}

export const getState = state => state.configurationReducer.state;
