import { createAction, createReducer } from '@reduxjs/toolkit'

const defaultState = {
    info: "",
}

export const infoReducer = (state = defaultState, { type, payload }) => {
  switch (type) {
    case "CLEAR_INFO":
      return {...state, info: ""};
    case "GET_INFO":
      return {...state, info: payload};
    default:
      return state;
  }
}
