import { createAction, createReducer } from '@reduxjs/toolkit'

export const GRID_POSITION = 0;
export const VERTICAL_POSITION = 1;

const defaultState = {
    state: GRID_POSITION
}

export const setPosition = createAction('CHANGE_POSITION');

export const configurationReducer = createReducer(defaultState, {
  [changePosition]: (state) => ({...state, state: !state.state})
})

// export const configurationReducer = (state = defaultState, action) => {
