import { createAction, createReducer } from '@reduxjs/toolkit'

const defaultState = {
    count: 0,
    blocked: false
}

export const increment = createAction('ADD_1');
export const setCount = createAction('SET_COUNT', payload => ({payload}));
export const toggleCounter = createAction('TOGGLE_COUNTER');

const actions = createActions({
    ADD_1: payload => ({payload}),
    SET_COUNT: payload => ({payload}),
    TOGGLE_COUNTER: payload => ({payload})
});

export const countReducer = createReducer(defaultState, {
  [increment]: (state) => ({...state, count: state.blocked ? state.count : state.count + 1}),
  [setCount]: (state, {payload}) => ({...state, count: state.blocked ? state.count : Number(payload)}),
  [toggleCounter]: (state) => ({...state, blocked: !state.blocked})
});
