import React from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import Button from "./Button";
import Container from "./Container";

const Form = styled.div`
    display: flex;
    flex-direction: row;
    margin: 14px;
`

const Addition = () => {
    const stateInput = { value: 0 };
    const dispatch = useDispatch()
    const count = useSelector(state => state.count)

    const addCount = () => {
        dispatch({type:"ADD_1", payload: 1})
    }

    const setCount = () => {
        dispatch({type:"SET_COUNT", payload: this.stateInput.value})
    }

    handleChange = () => {
        this.setState({value: event.target.value});
    }

    return (
        <Container>
            <Button onClick={() => addCount()}>ДОБАВИТЬ 1</Button>
            <Form>
                <Button onClick={() => setCount()}>УСТАНОВИТЬ</Button>
                <input type='number' value={this.stateInput.value} onChange={this.handleChange}></input>
            </Form>
            <Button>ПОЧИСТИТЬ ИНФУ В БЛОКЕ 4</Button>
        </Container>
    )
}

export default Addition;
