import React from "react";
import styled from "styled-components";
import Button from "./ui/Button";
import Container from "./ui/Container";
import { useDispatch, useSelector } from "react-redux";

const ContainerInfo = styled.div`
    padding-bottom: 20px;
`

const TextContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    margin-bottom: 4px;
`

const Information = () => {

    const position = useSelector(state => state.configuration.state);

    console.log(position)
    const positionText = React.useMemo(() => position === 0 ? "SETKA" : "VERTIKALNO", [position])

    return (
        <Container>
            <ContainerInfo>
                <TextContainer>
                    <p>ПОЛОЖЕНИЕ БЛОКОВ:</p><span>{positionText}</span>
                </TextContainer>
                <TextContainer>
                    <p>ЗНАЧЕНИЕ СЧЕТЧИКА:</p><span>{}</span>
                </TextContainer>
                <TextContainer>
                    <p>БЛОКИРОВКА СЧЕТЧИКА:</p><span>{}</span>
                </TextContainer>
            </ContainerInfo>
            <Button>ВЫВЕСТИ ИНФУ В БЛОК 4</Button>
        </Container>
    )
}

export default Information;
