import styled from "styled-components";
const state = useSelector(getState);

export const Container = styled.div `
    display: flex;
    border: 2px solid #000000;
    border-radius: 10px;
    padding: 14px;

    flex-direction:${props => {
        return (props.state == 0) ? 'column' : 'row'
    }}
`

export default Container;
