import React from "react";
import styled from "styled-components";
import Button from "./Button";
import Container from "./Container";
import { useDispatch, useSelector } from "react-redux";

const ContainerInfo = styled.div`
    padding-bottom: 20px;
`

const TextContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    margin-bottom: 4px;
`

const Information = () => {
    const count = useSelector(state => state.count.count);
    const dispatch = useDispatch();
    const info = useSelector(state => state.info)

    const stateInfo = {
        position: "сетка",
        count: count,
        block: "нет",
    }

    const addInfo = () => {
        dispatch(
            {type: "ADD_INFO", 
            payload1: stateInfo.position, 
            payload2: count, 
            payload3: stateInfo.block
        })
    }

    return (
        <Container>
            <ContainerInfo>
                <TextContainer>
                    <p>ПОЛОЖЕНИЕ БЛОКОВ:</p><span>{stateInfo.position.toUpperCase()}</span>
                </TextContainer>
                <TextContainer>
                    <p>ЗНАЧЕНИЕ СЧЕТЧИКА:</p><span>{stateInfo.count}</span>
                </TextContainer>
                <TextContainer>
                    <p>БЛОКИРОВКА СЧЕТЧИКА:</p><span>{stateInfo.block.toUpperCase()}</span>
                </TextContainer>
            </ContainerInfo>
            <Button onClick={() => addInfo()}>ВЫВЕСТИ ИНФУ В БЛОК 4</Button>
        </Container>
    )
}

export default Information;
