import { createAction, createReducer } from '@reduxjs/toolkit'

const defaultState = {
    count: 0,
    blocked: false
}

export const increment = createAction('ADD_1');
export const setCount = createAction('SET_COUNT', payload => ({payload}));
export const toggleCounter = createAction('TOGGLE_COUNTER');

export const countReducer = createReducer(defaultState, {
  [increment]: ({blocked, count}) => ({...state, count: blocked ? count : count + 1}),
  [setCount]: ({blocked, count}, {payload}) => ({...state, count: blocked ? count : Number(payload)}),
  [toggleCounter]: ({blocked}) => ({...state, blocked: !blocked})
});

