import React from "react";
import styled from "styled-components";
import Button from "./Button";
import Container from "./Container";
import { useSelector } from "react-redux";

const ContainerInfo = styled.div`
    margin-bottom: 30px;
`

const TextContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
    margin-bottom: 10px;
`

const Information = () => {
    const count = useSelector(state => state.count.count);

    const stateInfo = {
        position: "сетка",
        count: count,
        block: "нет",
    }

    return (
        <Container>
            <ContainerInfo>
                <TextContainer>
                    <p>ПОЛОЖЕНИЕ БЛОКОВ:</p><span>{stateInfo.position.toUpperCase()}</span>
                </TextContainer>
                <TextContainer>
                    <p>ЗНАЧЕНИЕ СЧЕТЧИКА:</p><span>{stateInfo.count}</span>
                </TextContainer>
                <TextContainer>
                    <p>БЛОКИРОВКА СЧЕТЧИКА:</p><span>{stateInfo.block.toUpperCase()}</span>
                </TextContainer>
            </ContainerInfo>
            <Button>ВЫВЕСТИ ИНФУ В БЛОК 4</Button>
        </Container>
    )
}

export default Information;
