import { fork, take, takeEvery } from "redux-saga/effects";
import { increment, setCount } from "../store/countReducer";
import countWhatcher from "./sagaCount";

function* loggerSaga(action) {
    alert(increment)
}

function* rootSaga() {
    yield fork(countWhatcher)
    yield takeEvery('*', loggerSaga)
}

export default rootSaga;
