import React from "react";
import styled from "styled-components";
import Button from "./Button";
import Container from "./Container";
import { useSelector } from "react-redux";

const TextContainer = styled.div`
    display: flex;
    flex-direction: row;
    align-items: center;
`

const Information = () => {

    const count = useSelector(state => state.count);
    const stateInfo = {
        position: "сетка",
        count: state.count.count,
        block: "да",
    }

    return (
        <Container>
            <TextContainer>
                <p>ПОЛОЖЕНИЕ БЛОКОВ:</p><span>{stateInfo.position.toUpperCase()}</span>
            </TextContainer>
            <TextContainer>
                <p>ЗНАЧЕНИЕ СЧЕТЧИКА:</p><span>{stateInfo.count}</span>
            </TextContainer>
            <TextContainer>
                <p>БЛОКИРОВКА СЧЕТЧИКА:</p><span>{stateInfo.block.toUpperCase()}</span>
            </TextContainer>
            <Button>ВЫВЕСТИ ИНФУ В БЛОК 4</Button>
        </Container>
    )
}

export default Information;
